import React, {useState, useEffect} from 'react';
import './App.css';
import { getAllPokemon, getPokemon } from "./services/PokemonService";

function App() {
  const [pokemonData, setPokemonData] = useState([]);
  const [nextUrl, setNextUrl] = useState('');
  const [preUrl, setPrevUrl] = useState('');
  const [loading, setLoading] = useState(true);
  const initUrl = 'https://pokeapi.co/api/v2/pokemon'; 

  useEffect(() => {
    async function fetchData() {
      let res = await getAllPokemon(initUrl);
      console.log(res);
      setNextUrl(res.next);
      setPrevUrl(res.previous);
      await loadingPokemon(res.results);
      setLoading(false);
    }
    fetchData();
  }, []); 

  const loadingPokemon = async (data) => {
    let _pokemonData = await Promise.all(data.map(async pokemon => {
      let pokemonRecord = await getPokemon(pokemon);
      return pokemonRecord;
    }))

    setPokemonData(_pokemonData)
  }

  return (
    <div className="App">
      { loading  ? <h1>Loading...</h1> : (
        <h1>Data is fetched</h1>
      )}
    </div>
  );
}

export default App;
