import React from 'react';
import './Card.css'
import 'bootstrap/dist/css/bootstrap.min.css';

function Card({ pokemon }) {

  const bgColor = {
    normal: "#BDBDAF",
    grass : "#7eb998",
    fire:"#f09990",
    water: "#8ab7e2",
    fighting: "#cc8c7f",
    flying: "#78A1FF",
    poison: "#d183c8",
    ground: " #f1db90",
    rock : "#e4d699",
    bug: "#d9e086",
    ghost: "#9995e4",
    electric: "#f1e48e",
    psychic: "#e493bf",
    ice : "#a2dee7",
    dragon: "#9c8fe5",
    dark : "#d8a992",
    steel: "#a7a3d8",
    fairy: "#ebacf0"
  }

  let color = "#000";
  const keys = Object.keys(bgColor);
  // if(keys.includes(pokeType)) {
  //     color = bgColor[pokeType];
  // }

  return (
    <div className="card-container col-1">
      <div className="card">
        <div className="front-card">

        </div>
      </div>

      <div className="Card_img">
        <img src={ pokemon.sprites.front_default } alt="" className="img-fluid"/>
      </div>
      <div className="Card_name">
        { pokemon.name }
      </div>
      <div className="Card_types">
        { pokemon.types.map(type => {
            return (
                <div className="Card__type">
                    {type.type.name}
                </div>
            )
        })}
      </div>
      <div className="Card_info">
        <div className="Card_data Card_data-weight">
          <p className="title">Weight: </p>
          <p>{ pokemon.weight }</p>
        </div>
        <div className="Card_data Card_data-height">
          <p className="title">Height: </p>
          <p>{ pokemon.height }</p>
        </div>
        <div className="Card_data Card_data-ability">
          <p className="title">Abilities: </p>
          { pokemon.abilities.map(ability => {
            return (
              <div className="Card_abilities">
                { ability.ability.name }
              </div>
            )
          })

          }
        </div>
      </div>
    </div>
  )
}

export default Card;
